'''
check if a string is valid if all chars occur just once or max one char occurs twice

'''


def isValid(s):

	hM = dict()
	firstVal = True
	defaultVal = 0
	count = 0

	for char in s:

		if char in hM:

			hM[char] += 1

		else:

			hM[char] = 1

	print(hM)

	for v in hM.values():

		if firstVal:
			defaultVal = v
			firstVal = False

		diff = abs(defaultVal - v)

		if diff > 0:

			count += 1

	if count <= 1:
		return 'YES'
	else:
		return 'NO'


s = ''
print(isValid(s))
